import styled from 'styled-components';

export const Container = styled.div`
	margin: auto;
	text-align: center;
	position: relative;
	top: 10px;

`;

export const Card = styled.div`
	padding: 10px;
	border-radius: 8px;
	border: 0.5px solid grey;
	background: white;
	.footer {
		background: #f1f1f1;
		button {
			margin: 8px;
		};
		width: 100%;
		padding: 0px;

		border-radius: 8px;
	}
`;
export const GridContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	position: relative;
`;


export const Filters = styled.div`
	width: 300px;
	height: 400px;
	border: 0.5px solid grey;
`;
