import React, { useRef } from 'react';
import { Container } from './styles';
import { Input, Button } from '@material-ui/core';
import axios from 'axios';

const serverUrl = 'http://localhost:3001'; 

const login = () => {
	const nameRef = useRef();
	const emailRef = useRef();
	const passwordRef = useRef();

	const handleSubmit = () => {
		if (!nameRef || !emailRef ) {
			alert('Please Enter the mandatory fields')
		}
		if(passwordRef.length < 4){
			alert('Please Enter password of more than 4 characters');
		}
		const response = axios.get(serverUrl + '/login/'); 
		console.log(response);
	}
return (
	<Container>
		<Input placeholder="Enter name" name="name" /><br />
		<Input placeholder="Enter email" name="email" /><br /><br />
		<Button variant="contained" color="primary" onClick={() => handleSubmit()}>Login</Button>
	</Container>
  )
}
export default login ;
