import React, { useState } from 'react';
import Link from 'next/link';
import { Container,Card, GridContainer, Footer, Filters } from './styles';
import { Button, Checkbox, Input } from '@material-ui/core';
import { data } from './data';

const Search = () => {
	const [checked, setChecked] = useState({
		driverName:'',
		langageSpoken: '',
	});
	console.log(checked.driverName);
	return (
		<Container>
				<div className="filterBox">
					<h3>Select Driver </h3>
						{
							data.map(item => {
								return (
									<div>
										<input type="radio" value={item.driverName}
											onChange={(e) => setChecked( ...checked, { driverName : e.target.value } ) } 
											name="driverNameRadio" />{item.driverName} {item.cost}
									</div>
								)
							})
						}
						<hr />
					<h3>Select Language</h3>
						{
							data.map(item => {
								return (
									<div>
										<input type="radio" value={item.spokenLanguage} name="languageRadio" 
											checked={true}
											handleChange={() => setChecked( ...checked, { languageSpoken: e.target.value } )} />
											{item.spokenLanguage}
									</div>
								)
							})
						}
				</div>
				<div>
					<Card>
						<GridContainer>
							<div>
								<h4>Booking Details</h4>
								<p>Origin </p>
							</div>
							<div>
								<div>
									Depart Date
									<p>28 Jan</p>
									<Button variant="contained" color="primary" >Modify</Button>
								</div>
							</div>
						</GridContainer>
					</Card><br /><br />
				</div>
		</Container>
	 )
}
export default Search ;
