import styled from 'styled-components';

export const Container = styled.div`
	margin: auto;
	width: 800px;
	text-align: center;
	position: relative;
	top: 10px;
`;

export const Card = styled.div`
	padding: 10px;
	border-radius: 8px;
	border: 0.5px solid grey;
	background: white;
`;
export const GridContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
`;

export const Footer = styled.div`
	background: #f1f1f1;
	button {
		margin: 8px;
	};
	padding: 0px;
	position: absolute;

	border-radius: 8px;
`;
