const initialState = {
	userEmail: '',
	isLoggedOut: true,
	isRegistered: false,
	origin: 'Bangalore',
	destination: '',
	departDate: '',
	count: 0,
};

export default function reducer (state = initialState, action) {
	switch (action.type) {
		case 'LOGIN_EMAIL' :
			return { ...state, userEmail: action.payload }
		case 'LOGOUT_USER':
			return { ...state, isLoggedIn : false };
		case 'IS_REGISTERED':
			return { ...state, isRegistered: true, userEmail: action.payload.email }
		case 'ADD_SEARCH_DATA':
			return { ...state,  }
		default :
			return state
	};
};
