export function loginUser(email, token) {
	return function (dispatch) {
		localStorage.setItem('token', token);
		localStorage.setItem('userEmail', email);
		dispatch(loginEmail(email, token));
	}
} 

export const login = (email, token) => ({
	type: 'LOGIN_EMAIL',
	payload: { email, token }
});
export const logout = userEmail => ({
	type: 'LOGOUT_USER',
});

export const register = ( userEmail ) => ({
	type: 'IS_REGISTERED',
});

export const add = (count) => ({
	type: 'ADD',
	payload: count,	
});
