import React, { useRef, useState, useEffect } from 'react';
import { Container, FormContainer, GridContainer } from './styles';
import { Input, Button } from '@material-ui/core';
import { useSelector } from 'react-redux';
import axios from 'axios';

const Booking = () => {
	const timeRef = useRef();
	const [user, setUser] = useState();
	const email = useSelector(state => state.userEmail);
	const name = useSelector(state => state.userName);
	const searchData = useSelector (state => state.searchData);
	const filterData = useSelector(state => state.filterData);
	const { driver, language } = filterData;
	const { origin , destination, departDate } = searchData;

	const serverUrl = 'http://localhost:3001'; 


	const handleBooking = () => {
		const pickupTime = timeRef.current.value;
		if(pickupTime.length === 0) {
			alert('Please enter the pick up time');
			return
		};
		const query = { driver, language, origin, destination, departDate, email };
		const response = axios.post((serverUrl + '/booking'), query);
		response.then((data) => console.log(data, 'kekfnekfnkffn')).catch((e) => console.log(e));
	}
return (
	<Container>
		<p>Traveller Details</p>
		<FormContainer>
			<GridContainer>
				<Input name="name" value="name" disabled />
				<Input name="email" value={email} disabled/>
			</GridContainer>
			<GridContainer>
				<Input name="origin" value="+91-7030226230" disabled/>
				<Input name="booking date" value={departDate} disabled />
			</GridContainer>
			<GridContainer>
				 <Input name="date" value={origin} disabled/>
				 <input name="time" type="time" placeholder="Select pickup time" ref={timeRef} />
			</GridContainer><br />
			<h3>Total Cost: </h3>
			<Button variant="contained" color="primary" onClick={() => handleBooking()}>Confirm Booking</Button>
		</FormContainer>
	</Container>
  )
}
export default Booking ;
