import React, { useState } from 'react';
import Link from 'next/link';
import { Container,Card, GridContainer, Footer, Filters } from './styles';
import { Button, Checkbox, Input } from '@material-ui/core';
import { data } from './data';
import Carlist from './carlist';
import { useSelector } from 'react-redux';

const Search = ( { origin, desitnation, departDate } ) => {
	const serverUrl = 'http:localhost:3001'


	const [driver, setDriver] = useState(' ');
	const [language, setLanguage] = useState(' ');

	const email = useSelector(state => state.userEmail);

	const handleBooking = () => {
		const response = axios.post(serverUrl + '/booking', 
			{ drive, language: language, origin: origin, destination: destination, departDate: departDate } ) 
	}
	return (
		<Container>
				<div className="filterBox">
					<h3>Select Driver </h3>
						{
							data.map(item => {
								return (
									<div>
										<input 
											type="radio" 
											value={item.driverName}
											onChange={(e) => setDriver( e.target.value ) } 
											name="driverNameRadio" />{item.driverName} {item.cost}
									</div>
								)
							})
						}
						<hr />
					<h3>Select Language</h3>
						{
							data.map(item => {
								return (
									<div>
										<input 
											type="radio" 
											value={item.spokenLanguage} 
											name="languageRadio" 
											onChange={(e) => setLanguage( e.target.value )} />
											{item.spokenLanguage}
									</div>
								)
							})
						}
				</div>
				<div>
					<Card>
						<GridContainer>
							<div>
								<h4>Booking Details</h4>
								<p>Origin </p>
							</div>
							<div>
								<div>
									Depart Date
									<Button variant="contained" color="primary" >Modify</Button>
								</div>
							</div>
						</GridContainer>
					</Card><br /><br />
					<Carlist />
				</div>
		</Container>
	 )
}
export default Search ;
