const initialState = {
	userEmail: ' ',
	isLoggedIn: false,
	isLoggedOut: true,
	isRegistered: false,
	searchDate : [
		{
			origin: 'Bangalore',
			destination: '',
			departDate: '',
		}
	],
	count: 0,
	token: '',
};

export default function reducer (state = initialState, action) {
	switch (action.type) {
		case 'LOGIN_EMAIL' :
			return { ...state, userEmail: action.payload, isLoggedIn: true }
		case 'LOGOUT':
			return Object.assign( { }, ...state, { isLoggedIn : false} );
		case 'IS_REGISTERED':
			return { ...state, isRegistered: true, userEmail: action.payload.email }
		case 'ADD_SEARCH_DATA':
			return { ...state, count: action.payload + 1 }
		default :
			return state
	};
};
