import React, { useRef } from 'react';
import { Container } from './styles';
import { Input, Button } from '@material-ui/core';
import axios from 'axios';
import { connect } from 'react-redux';
import { saveUser } from '../../redux/action'; 

const serverUrl = 'http://localhost:3001'; 

const login = () => {
	const nameRef = useRef();
	const emailRef = useRef();
	const passwordRef = useRef();

	const handleSubmit = () => {
		const email = emailRef.current.value;
		const password = passwordRef.current.value;
		if (email.length < 0 ) {
			alert('Please Enter the mandatory fields')
		}
		if(password.length < 4){
			alert('Please Enter password of atleast 4 characters');
		};
		const response = axios.get(serverUrl + '/login'); 
		response.then((data) => {
			if(data.status == 200) {
				console.log(userEmail);
			}
		}).catch((e) => console.log(e));
	}
return (
	<Container>
		<Input placeholder="Enter email" name="email" type="email" inputRef={emailRef} /><br />
		<Input placeholder="Enter password" name="password"  type="password" inputRef={passwordRef} /><br /><br />
 		<Button variant="contained" color="primary" onClick={() => handleSubmit()}>Login</Button>
	</Container>
  )
};
const mapStateToProps = (state) => {
	const { userEmail } = state;
};

export default connect(mapStateToProps)(Login) ;
