export const LoginUser = userEmail => {
	return dispath =>{
		localStorage.setItem('userEmail', userEmail);
		dispatch(loginEmail(userEmail));
	}
};

export const loginEmail = userEmail => ({
	type: 'LOGIN_EMAIL',
	payload: userEmail,
});
export const logout = userEmail => ({
	type: 'LOGOUT_USER',
});

export const register = ( userEmail ) => ({
	type: 'IS_REGISTERED',
});

export const add = (count) => ({
	type: 'ADD',
	payload: count,	
});
