const initialState = {
	isLoggedIn: false,
	userEmail: '',
	isLoggedOut: true,
	isRegistered: false,
	count: 0,
};

export default function reducer (state = initialState, action) {
	switch (action.type) {
		case 'LOGIN_USER' :
			return { ...state, userEmail: payload, isLoggedIn: true }
		case 'LOGOUT_USER':
			return { ...state, isLoggedOut : true };
		case 'IS_REGISTERED':
			return { ...state, isAuthenticated: true}
		case 'ADD':
			return {...state }
		default :
			return state
	};
};
