import React, { useRef } from 'react';
import { Container } from './styles';
import { Input, Button } from '@material-ui/core';
import axios from 'axios';

const serverUrl = 'http://localhost:3001'; 

const login = () => {
	const nameRef = useRef();
	const emailRef = useRef();
	const passwordRef = useRef();

	const handleSubmit = () => {
		if (!nameRef || !emailRef ) {
			alert('Please Enter the mandatory fields')
		}
		if(passwordRef.current.length < 4){
			alert('Please Enter password of atleast 4 characters');
		}
		const response = axios.get(serverUrl + '/login'); 
		response.then((data) => console.log(data)).catch((e) => console.log(e));
	}
return (
	<Container>
		<Input placeholder="Enter name" name="name" ref={nameRef} /><br />
		<Input placeholder="Enter email" name="email" ref={emailRef} /><br />
		<Input placeholder="Enter password" name="password"  type="password" ref={passwordRef} /><br /><br />
 		<Button variant="contained" color="primary" onClick={() => handleSubmit()}>Login</Button>
	</Container>
  )
}
export default login ;
