import React, { useState } from 'react';
import Link from 'next/link';
import { Container,Card, GridContainer, Footer, Filters } from './styles';
import { Button, Checkbox } from '@material-ui/core';
import { data } from './data';

const Search = () => {
	const [checked, setChecked] = useState({
		driverName: true,
		languageSpoken: true,
	});
	const handleChange = (e) => {
		setChecked({ ...checked, [name]: e.target.value })
	}
	return (
		<Container>
			<GridContainer>
				<div className="filterBox">
					<p>Select Driver </p>
						{
							data.map(item => {
								return (
									<div>
										<Checkbox name={item.driverName} onChange={handleChange} lael/>
										<p>{item.cost}</p>
									</div>
								)
							})
						}
					<p>Select Language</p>
						{
							data.map(item => {
								return (
									<Checkbox value={item.spokenLanguage} handleChange={handleChange} />
								)
							})
						}
				</div>
				<div>
					<Card>
						<GridContainer>
							<div>Booking details</div>
							<div>
								<div>
									Depart Date
									<Button variant="contained" color="primary" >Modify</Button>
								</div>
							</div>
						</GridContainer>
					</Card><br /><br />
					<Card>
						<GridContainer>
							<div>
								<img src="/search_page_car_img.png" width="80px" height="80px"/>
							</div>
							<div>
								<Link href="/booking-payment"><Button variant="contained" color="primary">Book Now</Button></Link>
							</div>
						</GridContainer>
						<div className="footer">
							<GridContainer>
								<Button color="primary">Terms & Services</Button>
								<Button color="primary">Fare Details</Button>
							</GridContainer>
						</div>
					</Card>
				</div>

			</GridContainer>
		</Container>
	 )
}
export default Search ;
