import React, { useRef } from 'react';
import Link from 'next/link';
import { Container } from './styles';
import { Input, Button } from '@material-ui/core';
import Router from 'next/link';

const Oneway = ( { state, setState  } ) => {
	const origin = useRef();
	const destination = useRef();
	const departDate = useRef(); 
	const handleSubmit = () => {
		console.log(origin.current.value);
		const destination = destination.current.value;
		const departDate = departDate.current.value;
		if (origin.length || destination.length || departDate.length === 0 ){
			alert('Please Enter the required field');
		}
		const query = {
			origin,
			destination,
			departDate,
		};
		Router.push({
			path: '/register',
			query: query,
		});
	};
	return (
		<Container>
			<Input placeholder="Select Origin" type="city" value="Bangalore" inputRef={origin} />
			<Input placeholder="Select Destination" inputRef={destination} />
			<Input placeholder="Enter date" type="date" inputRef={departDate} />
			<Input placeholder="Enter return date" type="date" disabled onClick={() => setState('roundtrip')}/>
			<Button variant="contained" color="primary" onClick={handleSubmit}>Book Now</Button>
		</Container>
  )
}
export default Oneway ;
