import React, { useRef } from 'react';
import { Container } from './styles';
import { Input, Button } from '@material-ui/core';
import axios from 'axios';
import { connect } from 'react-redux';
import { login } from '../../redux/action'; 
import { useDispatch } from 'react-redux';


const serverUrl = 'http://localhost:3001'; 

const mapStateToProps = (state) => {
	const { userEmail } = state;
	return ({ userEmail });
};
const Login = () => {
	const nameRef = useRef();
	const emailRef = useRef();
	const passwordRef = useRef();
	const dispatch = useDispatch();

	const handleSubmit = ( ) => {
		const email = emailRef.current.value;
		const password = JSON.stringify(passwordRef.current.value);
		if (email.length < 0 ) {
			alert('Please Enter the mandatory fields')
		}
		if(password.length < 4){
			alert('Please Enter password of atleast 4 characters');
		};
		const response = axios.post(serverUrl + '/login', { body: { email, password } }); 
		response.then((data) => {
			console.log(data);
			// if(data.status == 200) {
			// 	console.log(data);
			// 	// dispatch(login(data.user.email));
			// 	// router.push('/car-search');
			// }
		}).catch((e) => console.log(e));
	}
return (
	<Container>
		<Input placeholder="Enter email" name="email" type="email" inputRef={emailRef} /><br />
		<Input placeholder="Enter password" name="password"  type="password" inputRef={passwordRef} /><br /><br />
 		<Button variant="contained" color="primary" onClick={() => handleSubmit()}>Login</Button>
	</Container>
  )
};

export default connect(mapStateToProps, null)(Login) ;
